import React, { useState } from 'react';
import axios from 'axios';
import './App.css';  // We'll move the CSS to a separate file for clarity

function App() {
  const [file, setFile] = useState(null);
  const [colors, setColors] = useState([]);
  const [percentages, setPercentages] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = () => {
    const formData = new FormData();
    formData.append('image', file);

    axios.post('http://localhost:5000/api/upload', formData)
      .then(response => {
        setColors(response.data.colors);
        setPercentages(response.data.percentages);
      })
      .catch(error => {
        console.error("There was an error uploading the file!", error);
      });
  };

  return (
    <div className="App">
      <h1>Upload Image to Get Dominant Colors</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>

      {colors.length > 0 && (
        <div className="color-container">
          {colors.map((color, index) => (
            <div key={index} className="color-block" style={{ width: `${percentages[index]}%`, backgroundColor: color }}>
              <p className="hex-code">{color}</p>
              <p>{percentages[index]}%</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
