import React, { useState, useRef } from 'react';
const KMeans = require('ml-kmeans');
import './App.css';

function App() {
  const [colors, setColors] = useState([]);
  const [percentages, setPercentages] = useState([]);
  const [imageURL, setImageURL] = useState(null);
  const canvasRef = useRef(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const url = URL.createObjectURL(file);
      setImageURL(url);
      extractColors(url);
    }
  };

  const extractColors = (url) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.crossOrigin = 'Anonymous';
    img.src = url;

    img.onload = () => {
      // Resize image for faster processing
      const MAX_WIDTH = 200;
      const scaleSize = MAX_WIDTH / img.width;
      canvas.width = MAX_WIDTH;
      canvas.height = img.height * scaleSize;
      ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

      // Get pixel data
      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      const pixels = imageData.data;
      const pixelData = [];

      for (let i = 0; i < pixels.length; i += 4) {
        const r = pixels[i];
        const g = pixels[i + 1];
        const b = pixels[i + 2];
        // Ignore transparent pixels
        const a = pixels[i + 3];
        if (a > 0) {
          pixelData.push([r, g, b]);
        }
      }

      // Run KMeans
      const numberOfClusters = 5;
      const { clusters, centroids } = KMeans(pixelData, numberOfClusters);

      // Calculate percentages
      const counts = {};
      clusters.forEach((cluster) => {
        counts[cluster] = (counts[cluster] || 0) + 1;
      });

      const total = pixelData.length;
      const colorData = centroids.map((centroid, index) => {
        const percentage = ((counts[index] || 0) / total) * 100;
        const hex = rgbToHex(centroid[0], centroid[1], centroid[2]);
        return { hex, percentage };
      });

      // Sort colors by percentage descending
      colorData.sort((a, b) => b.percentage - a.percentage);

      setColors(colorData.map((c) => c.hex));
      setPercentages(colorData.map((c) => c.percentage.toFixed(2)));
    };
  };

  const rgbToHex = (r, g, b) => {
    return (
      '#' +
      [r, g, b]
        .map((x) => {
          const hex = x.toString(16);
          return hex.length === 1 ? '0' + hex : hex;
        })
        .join('')
    ).toUpperCase();
  };

  return (
    <div className="App">
      <h1 className="title">Image Color Palette</h1>
      <input type="file" accept="image/*" onChange={handleImageUpload} />

      {imageURL && (
        <div className="image-preview">
          <img src={imageURL} alt="Uploaded" />
        </div>
      )}

      {colors.length > 0 && (
        <div className="color-container">
          {colors.map((color, index) => (
            <div
              key={index}
              className="color-block"
              style={{ backgroundColor: color, flexBasis: `${percentages[index]}%` }}
            >
              <div className="hex-code">{color}</div>
              <div className="percentage">{percentages[index]}%</div>
            </div>
          ))}
        </div>
      )}

      {/* Hidden canvas for processing */}
      <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>
    </div>
  );
}

export default App;
